package structural.proxy

class ImageProxy(user: String, password: String): IGraphic {

    private var downloaded: Boolean = false
    private var image: IGraphic = RemoteImage("data:image/jpeg;base64,")
    val access: Boolean
        get() {
            if(!field)
                println("Error en la autenticación")
            return field
        }


    init {
        access = (user=="1234" && password == "4321")
    }

    override fun draw() {
        println("ImageProxy: draw")
        if(!access)
            return
        if(checkLoad())
            image.draw()
    }

    override fun getExtents(): String {
        println("ImageProxy: getExtent")
        if(!access)
            return ""
        return image.getExtents()
    }

    override fun load(): Boolean {
        println("ImageProxy: load")
        if(!access)
            return false

        if(image.load())
            downloaded = true

        return downloaded
    }

    override fun store(path: String): Boolean {
        println("ImageProxy: store")
        if(!access)
            return false

        return if(checkLoad())
            image.store(path)
        else
            false
    }

    override fun getImage() : String {
        println("ImageProxy: getImage")
        if(!access)
            return ""

        return image.getImage()
    }

    private fun checkLoad(): Boolean {
       return if (!downloaded)
            image.load()
        else
            true
    }
}